import { Component, OnInit } from '@angular/core';
import { CategoryService } from '../services/category.service';
import { Category } from '../models/category.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-category-list',
  templateUrl: './category-list.component.html',
  styleUrls: ['./category-list.component.css']
})
export class CategoryListComponent implements OnInit {


categories$?:Observable<Category[]>;


  constructor(private categoryService:CategoryService) {
  }
  ngOnInit(): void {
this.categories$=    this.categoryService.getAllCategory();

  }



}
<div class="container">
<h1 class="mt-3">Category List</h1>

<div
class=" d-flex justify-content-end mt-3">
<a [routerLink]="['/admin/categories/add']" class="btn btn-primary">Add Category</a></div>


<ng-container *ngIf="categories$ |async as  categories">
    <table class="table table-bordered mt-3">
        <thead>
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Url Handle</th>
                <th>Url Handle</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let item of categories">
                <td>{{item.id}}</td>
                <td>{{item.name}}</td>
                <td>{{item.urlHandle}}</td>  <!-- This line should be inside the <tr> -->
                    <td>
                        <a class="btn btn light" [routerLink]="['/admin/categories',item.id]">Edit</a>
                    </td>
            </tr>
        </tbody>
    </table>
</ng-container>


</div>
